import { useState } from "react";
import InlinePost from "../InlinePost";
import { Post } from "../../Types/types"
import Layout from "../Layout";
import "./styles.scss";

const MockData = [
    {
        id: '1',
        title: 'Post1',
        body: 'Lorem Ipsum Dolorem Ipsum Dolorem 1111',
        date: "23/12/2021"
    },
    {
        id: '2',
        title: 'Post 2',
        body: 'Lorem Ipsum Dolorem Ipsum Dolorem 2222',
        date: "23/12/2021"
    }
];

function BlogPostList() {
    const [postList, setPostList] = useState<Post[]>(MockData);

    const addPost = (post: Post) => {
        setPostList([...postList, post]);
    }

    return (
        <Layout>
            <div className="list-container">
                <h2>Posts</h2>
                {MockData.map(post => {
                    return <InlinePost key={post.id} post={post} />
                })}
            </div>
        </Layout>
    );
}

export default BlogPostList;
