import InlinePost from "../InlinePost";
import { Post } from "../../Types/types"
import InfiniteScroll from 'react-infinite-scroll-component';
import "./styles.scss";

interface BlogPostListProps {
    postList: Post[];
    fetchNextPage: () => {};
    totalCount: number;
}

function BlogPostList({ postList, fetchNextPage, totalCount }: BlogPostListProps) {

    return (
        <div className="list-container" id="scrollableDiv">
            <h2>Posts</h2>
            <InfiniteScroll
                dataLength={postList.length}
                next={fetchNextPage}
                hasMore={totalCount > postList.length}
                loader={<p>Loading...</p>}
                endMessage={<p>No more data to load.</p>}
                scrollableTarget="scrollableDiv"
            >
                {postList.map((post, i) => {
                    return <InlinePost key={post.id} index={i} post={post} />
                })}
            </InfiniteScroll>
        </div>
    );
}

export default BlogPostList;
