import React, { useEffect, useState } from "react";
import FormModal from "../FormModal";
import BlogPostList from "../BlogPostList";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useQuery, useMutation } from '@apollo/client';
import { GET_POSTS } from "./queries";
import { DELETE_POST, CREATE_POST, UPDATE_POST } from "./mutations";
import { Post } from "../../Types/types"
import "./styles.scss";
import Layout from "../Layout";


function AppCont() {
  const [show, setShow] = useState(false);
  const [page, setPage] = useState(1);
  const [editedPost, setEditedPost] = useState<Post | undefined>(undefined);
  const [totalCount, setTotalCount] = useState(0);
  const [postList, setPostList] = useState<Post[]>([]);

  const addPost = (post: Post) => {
    setPostList([post, ...postList]);
  }

  const { loading, error, data, fetchMore } = useQuery(GET_POSTS, {
    variables: {
      options: {
        paginate: {
          page: page,
          limit: 20
        }
      }
    }
  });
  const [createPost, { data: createPostData, loading: createPostLoading, error: createPostError }] = useMutation(CREATE_POST);

  const [deletePost, { loading: deleting, error: deleteError }] = useMutation(DELETE_POST, {
    onCompleted: () => {
    }
  });

  const [updatePost, { loading: updating, error: updateError }] = useMutation(UPDATE_POST);


  const handleDelete = async (deleingPost: Post) => {

    // deleting
    const updatedPosts = postList.map(post =>
      post.id === deleingPost.id ? { ...post, deleting: true } : post
    );
    setPostList(updatedPosts);
    try {
      const result = await deletePost({ variables: { id: deleingPost.id } });
      setPostList(postList.filter(p => p.id != deleingPost.id));

    } catch (e) {
      console.error(e);
    }
  };

  const handleEditClick = (post: Post) => {
    setEditedPost(post);
    handleShow();
  }

  const handleUpdatePost = async (title: string, body: string) => {
    if (!editedPost) return;
    try {
      await updatePost({
        variables: {
          id: editedPost.id,
          input: { title, body }
        }
      });
      const updatedPosts = postList.map(post =>
        post.id === editedPost.id ? { id: editedPost.id, title, body } : post
      );
      setPostList(updatedPosts);
    } catch (e) {
      console.error(e);
    }
  }

  useEffect(() => {
    if (!data?.posts?.data) return;
    setPostList([...postList, ...data.posts.data]);
    setTotalCount(data.posts.meta.totalCount);
  }, [loading]);

  const handleClose = () => {
    setShow(false);
    setEditedPost(undefined);
  };
  const handleShow = () => setShow(true);

  const fetchNextPage = () => {
    const newPage = page + 1;
    setPage(newPage);
    fetchMore({
      variables: {
        options: {
          paginate: {
            page: newPage,
            limit: 20
          }
        }
      }
    })
  };

  const savePendig = async () => {
    const pendingPosts = postList.filter(post => { return !post.id });
    try {
      for (const post of pendingPosts) {
        const response = await createPost({
          variables: {
            input: post
          }
        });
        const createdPostId = response.data.createPost.id;
        post.id = createdPostId + Date.now();
      }
    } catch (e) {
      console.error(e);
    }

  }

  return (
    <div className="app-container">
      <Layout savePendig={savePendig} handleShow={handleShow} />
      <div className="content">
        <BlogPostList onEdit={handleEditClick} onDelete={handleDelete} totalCount={totalCount} fetchNextPage={fetchNextPage} postList={postList} />
      </div>

      <FormModal updatePost={handleUpdatePost} post={editedPost} show={show} handleClose={handleClose} addPost={addPost} />
    </div >
  );
}

export default AppCont;
