import React, { useEffect, useState } from "react";
import FormModal from "../FormModal";
import BlogPostList from "../BlogPostList";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useQuery, gql } from '@apollo/client';
import { Post } from "../../Types/types"
import "./styles.scss";

const GET_POSTS = gql`
    query GetPosts($options: PageQueryOptions) {
        posts(options: $options) {
            data {
                id
                title
                body
            }
            meta {
                totalCount
            }
        }
    }
`;


function Layout() {
    const [show, setShow] = useState(false);
    const [page, setPage] = useState(1);
    const [postList, setPostList] = useState<Post[]>([]);

    const addPost = (post: Post) => {
        setPostList([...postList, post]);
    }

    const { loading, error, data, fetchMore } = useQuery(GET_POSTS, {
        variables: {
            options: {
                paginate: {
                    page: page,
                    limit: 20
                }
            }
        }
    });

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    console.log(data);
    const posts = data.posts.data;
    const totalCount = data.posts.meta.totalCount;
    console.log(totalCount);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const fetchNextPage = () => {
        const newPage = page + 1;
        setPage(newPage);
        fetchMore({
            variables: {
                options: {
                    paginate: {
                        page: newPage,
                        limit: 20
                    }
                }
            }
        })
    };

    const savePendig = () => {

    }

    return (
        <div className="app-container">
            <div className="header">
                <h1>Blog Post App</h1>
                <div className="orange-circle"></div>
                <div className="grey-scquare"></div>
            </div>
            <div className="menu">
                <div className="button-container">
                    <div className="button">
                        <div className="action" onClick={handleShow}>+</div>
                        <span>Add New</span>
                    </div>
                    <div className="button">
                        <div className="action" onClick={savePendig}><FontAwesomeIcon icon={faSave} /></div>
                        <span>Save Pending</span>
                    </div>
                </div>
            </div>
            <div className="content">
                <BlogPostList totalCount={totalCount} fetchNextPage={fetchNextPage} postList={posts} />
            </div>

            <FormModal show={show} handleClose={handleClose} addPost={addPost} />
        </div >
    );
}

export default Layout;
