import { Post } from "../../Types/types";

import "./styles.scss";

interface InlinePostPropsTypes {
    post: Post;
    index: number;
}

function InlinePost({ post, index }: InlinePostPropsTypes) {
    return <div className="post-inline">
        <div className={`card-image card-image-${index % 5}`}>
            <h3>{post?.title && post.title[0]}</h3>
        </div>
        <div className="card-content">
            <h2 className="card-title">{post.title}</h2>
            <p className="card-text">{post.body}</p>
        </div>
        <div className="card-actions">
            <span className="post-date">Posted: {post.date}</span>
        </div>
    </div>;
}

export default InlinePost;
