import React, { useEffect, useState } from "react";
import FormModal from "../FormModal";
import BlogPostList from "../BlogPostList";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useQuery, gql } from '@apollo/client';
import { Post } from "../../Types/types"
import "./styles.scss";

const GET_POSTS = gql`
    query GetPosts($options: PageQueryOptions) {
        posts(options: $options) {
            data {
                id
                title
                body
            }
            meta {
                totalCount
            }
        }
    }
`;


const MockData = [
    {
        id: '1',
        title: 'Post1',
        body: 'Lorem Ipsum Dolorem Ipsum Dolorem 1111',
        date: "23/12/2021"
    },
    {
        id: '2',
        title: 'Post 2',
        body: 'Lorem Ipsum Dolorem Ipsum Dolorem 2222',
        date: "23/12/2021"
    }
];


function Layout() {
    const [show, setShow] = useState(false);
    const [postList, setPostList] = useState<Post[]>([]);

    const addPost = (post: Post) => {
        setPostList([...postList, post]);
    }

    useEffect(() => {
        const { loading, error, data } = useQuery(GET_POSTS, {
            variables: {
                options: {
                    paginate: {
                        page: 1,
                        limit: 5
                    }
                }
            }
        });
        // if (loading) return <p>Loading...</p>;
        // if (error) return <p>Error :(</p>;
        setPostList(data.posts.data);

    }, []);



    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const savePendig = () => {

    }

    return (
        <div className="app-container">
            <div className="header">
                <h1>Blog Post App</h1>
                <div className="orange-circle"></div>
                <div className="grey-scquare"></div>
            </div>
            <div className="menu">
                <div className="button-container">
                    <div className="button">
                        <div className="action" onClick={handleShow}>+</div>
                        <span>Add New</span>
                    </div>
                    <div className="button">
                        <div className="action" onClick={savePendig}><FontAwesomeIcon icon={faSave} /></div>
                        <span>Save Pending</span>
                    </div>
                </div>
            </div>
            <div className="content">
                <BlogPostList postList={postList} />
            </div>

            <FormModal show={show} handleClose={handleClose} addPost={addPost} />
        </div >
    );
}

export default Layout;
