import React, { useEffect, useState } from "react";
import FormModal from "../FormModal";
import BlogPostList from "../BlogPostList";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Post } from "../../Types/types"
import "./styles.scss";

const GET_POSTS = gql`
    query GetPosts($options: PageQueryOptions) {
        posts(options: $options) {
            data {
                id
                title
                body
            }
            meta {
                totalCount
            }
        }
    }
`;

const CREATE_POST = gql`
  mutation CreatePost($input: CreatePostInput!) {
    createPost(input: $input) {
      id
      title
      body
    }
  }
`;


function Layout() {
    const [show, setShow] = useState(false);
    const [page, setPage] = useState(1);
    const [totalCount, setTotalCount] = useState(0);
    const [postList, setPostList] = useState<Post[]>([]);

    const addPost = (post: Post) => {
        setPostList([post, ...postList]);
    }

    const { loading, error, data, fetchMore } = useQuery(GET_POSTS, {
        variables: {
            options: {
                paginate: {
                    page: page,
                    limit: 20
                }
            }
        }
    });


    useEffect(() => {
        if (!data?.posts?.data) return;
        setPostList([...postList, ...data.posts.data]);
        setTotalCount(data.posts.meta.totalCount);
    }, [loading]);


    console.log(data);
    console.log(totalCount);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const fetchNextPage = () => {
        const newPage = page + 1;
        setPage(newPage);
        fetchMore({
            variables: {
                options: {
                    paginate: {
                        page: newPage,
                        limit: 20
                    }
                }
            }
        })
    };

    const savePendig = async () => {
        const [createPost, { data, loading, error }] = useMutation(CREATE_POST);

        const pendingPosts = postList.filter(post => { return !post.id });

        try {
            await createPost({
                variables: {
                    input: pendingPosts[0]
                }
            });
            // Handle success (e.g., clear form, show message)
        } catch (e) {
            // Handle error
        }
    }

    return (
        <div className="app-container">
            <div className="header">
                <h1>Blog Post App</h1>
                <div className="orange-circle"></div>
                <div className="grey-scquare"></div>
            </div>
            <div className="menu">
                <div className="button-container">
                    <div className="button">
                        <div className="action" onClick={handleShow}>+</div>
                        <span>Add New</span>
                    </div>
                    <div className="button">
                        <div className="action" onClick={savePendig}><FontAwesomeIcon icon={faSave} /></div>
                        <span>Save Pending</span>
                    </div>
                </div>
            </div>
            <div className="content">
                <BlogPostList totalCount={totalCount} fetchNextPage={fetchNextPage} postList={postList} />
            </div>

            <FormModal show={show} handleClose={handleClose} addPost={addPost} />
        </div >
    );
}

export default Layout;
